import React, { useState } from "react";
import { Alert, Modal, StyleSheet, Text, Pressable, View, SafeAreaView, TextInput, Button } from "react-native";

const Navigation = () => {

  const titles = ['About', 'Sermons', 'Events', 'Me']

  return (
    <View style={styles.navContainer}>
      {
        titles.map((title, index) => {
          return (
            <Pressable key={`nav-${index}`}>
              <Text style={styles.navText}>{title}</Text>
            </Pressable>
          )
        })
      }
    </View>
  );
};

const styles = StyleSheet.create({
  navContainer: {
    flex: 1,
    display: 'flex',
    flexDirection: 'row',
    justifyContent: "space-around",
    alignItems: "center",
  },
  navText: {
    color: 'white',
    fontSize: 18
  }
});

export default Navigation;
