import React from 'react';
import { ImageBackground, StyleSheet, View, Text, SafeAreaView } from 'react-native';

import bgSrc from '../assets/background.jpg';


function WelcomeScreen(props) {

    return (
        <SafeAreaView>
            <ImageBackground
                styles={styles.backgroundImage}
                source={bgSrc}
            >
            </ImageBackground>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    backgroundImage: {
        flex: 1,
        width: 100,
        height: 100,
        justifyContent: 'center',
        alignItems: 'center',
        resizeMode: 'cover',
    },
    loginButton: {
        width: '100%',
        height: 70,
        backgroundColor: '#fc5c65'
    }
})

export default WelcomeScreen;
